package www.jca.com.may05;

public class RelaTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a,b;
		a='A';
		b='B';
		System.out.println((int)a);
		System.out.println((int)b);
		System.out.println("a>b->"+(a>b));
		System.out.println("a<b->"+(a<b));
		System.out.println("a==b->"+(a==b));
		System.out.println("a!=b->"+(a!=b));
	}

}
